import React, { useState } from "react";
import {
  Tree,
  NodeModel,
  DragLayerMonitorProps,
} from "@minoru/react-dnd-treeview";
import { CustomData } from "./types";
import { CustomNode } from "./CustomNode";
import { CustomDragPreview } from "./CustomDragPreview";
import { makeStyles } from "@mui/styles";
import { Theme } from "@mui/material";
import SampleData1 from "./sample_data1.json";
import SampleData2 from "./sample_data2.json";
import SampleData3 from "./sample_data3.json";

const useStyles = makeStyles((theme: Theme) => ({
  app: {
    height: "100%",
    "& ul": {
      listStyle: "none",
    },
    "& *": {
      margin: 0,
      padding: 0,
    },
  },
  treeRoot: {
    height: "100%",
  },
  draggingSource: {
    opacity: ".3",
  },
  dropTarget: {
    backgroundColor: "#e8f0fe",
  },
}));

function TreeView({ data: number }) {
  const classes = useStyles();
  const [treeData, setTreeData] = useState<NodeModel[]>(SampleData);
  const handleDrop = (newTree: NodeModel[]) => setTreeData(newTree);

  return (
    <div className={classes.app}>
      <Tree
        tree={treeData}
        rootId={0}
        render={(node: NodeModel<CustomData>, { depth, isOpen, onToggle }) => (
          <CustomNode
            node={node}
            depth={depth}
            isOpen={isOpen}
            onToggle={onToggle}
          />
        )}
        dragPreviewRender={(
          monitorProps: DragLayerMonitorProps<CustomData>
        ) => <CustomDragPreview monitorProps={monitorProps} />}
        onDrop={handleDrop}
        classes={{
          root: classes.treeRoot,
          draggingSource: classes.draggingSource,
          dropTarget: classes.dropTarget,
        }}
        initialOpen={true}
      />
    </div>
  );
}

export default TreeView;
